package _01_opearation;

public class T03_compare {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int y1 = 17;
		int y2 = 32;
		
		boolean answer1 = y1 == y2; // 두 값이 같나
		boolean answer2 = y1 != y2; // 두 값이 다르나
		
		System.out.println("대답 : " + answer1);
		System.out.println("대답 : " + answer2);
	}
}
